+test("legacy filters should be accessible after migration", async ({ page }) => {
+  // Setup legacy filter in localStorage
+  await page.evaluate(() => {
+    localStorage.setItem("saved-filters", JSON.stringify([{ id: "1", name: "Legacy Filter", filters: { date: "recent" } }]));
+  });
